import React, { useState, useEffect } from 'react';
import ProductList from './productList';

const App = () => {
  const productData = [
    {
      Ean: 'EAN001',
      nombre_producto: 'Producto 1',
      datos_query: [
        { Market: 'Market1', normal_price: 10 },
        { Market: 'Market2', normal_price: 15 }
      ],
      markets_diferentes: new Set(['Market1', 'Market2']),
      rango_precios: [10, 15]
    },
    {
      Ean: 'EAN002',
      nombre_producto: 'Producto 2',
      datos_query: [
        { Market: 'Market2', normal_price: 15 },
        { Market: 'Market1', normal_price: 20 }
      ],
      markets_diferentes: new Set(['Market2', 'Market1']),
      rango_precios: [15, 20]
    },
    {
      Ean: 'EAN003',
      nombre_producto: 'Producto 3',
      datos_query: [
        { Market: 'Market1', normal_price: 20 },
        { Market: 'Market2', normal_price: 30 }
      ],
      markets_diferentes: new Set(['Market1', 'Market2']),
      rango_precios: [20, 30]
    },
  ];

  const [filteredProducts, setFilteredProducts] = useState([]);
  const [filter, setFilter] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // Inicializar filteredProducts con todos los productos al cargar la aplicación
    setFilteredProducts(productData);
  }, [productData]);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex < filteredProducts.length - 1 ? prevIndex + 1 : prevIndex
      );
    }, 1000);

    return () => clearInterval(timer);
  }, [currentIndex, filteredProducts]);

  const applyFilter = () => {
    const filtered = productData.filter((product) =>
      product.nombre_producto.toLowerCase().includes(filter.toLowerCase())
    );
    setFilteredProducts(filtered);
    setCurrentIndex(0); // Reiniciar el índice al aplicar el filtro
  };

  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  return (
    <div>
      <h1>Lista de Productos</h1>
      <input
        type="text"
        placeholder="Filtrar por nombre"
        value={filter}
        onChange={handleFilterChange}
      />
      <button onClick={applyFilter}>Aplicar Filtro</button>
      <ProductList products={filteredProducts.slice(0, currentIndex + 1)} />
    </div>
  );
};


