import React, { useState, useEffect } from 'react';
import ProductList from './productList';

const App = () => {
  const productData = [
    {
      Ean: 'EAN001',
      nombre_producto: 'Producto 1',
      datos_query: [
        { Market: 'Market1', normal_price: 10 },
        { Market: 'Market2', normal_price: 15 }
      ],
      markets_diferentes: new Set(['Market1', 'Market2']),
      rango_precios: [10, 15]
    },
    {
      Ean: 'EAN002',
      nombre_producto: 'Producto 2',
      datos_query: [
        { Market: 'Market2', normal_price: 15 },
        { Market: 'Market1', normal_price: 20 }
      ],
      markets_diferentes: new Set(['Market2', 'Market1']),
      rango_precios: [15, 20]
    },
    {
      Ean: 'EAN003',
      nombre_producto: 'Producto 3',
      datos_query: [
        { Market: 'Market1', normal_price: 20 },
        { Market: 'Market2', normal_price: 30 }
      ],
      markets_diferentes: new Set(['Market1', 'Market2']),
      rango_precios: [20, 30]
    },
  ];

  const [products, setProducts] = useState(productData);
  const [filter, setFilter] = useState('');

  const applyFilter = () => {
    const filteredProducts = productData.filter((product) =>
      product.nombre_producto.toLowerCase().includes(filter.toLowerCase())
    );
    setProducts(filteredProducts);
  };

  useEffect(() => {
    let interval;

    if (products.length > 0 && filter === "") {
      interval = setInterval(() => {
        setProducts((prevProducts) => {
          if (prevProducts.length > 0) {
            const newProducts = [...prevProducts];
            newProducts.pop();
            return newProducts;
          }
          return prevProducts;
        });
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [products]);

  useEffect(() => {
    return () => clearInterval(interval);
  }, []);

  const handleFilterChange = event => {
    setFilter(event.target.value);
  };

  return (
    <div>
      <h1>Lista de Productos</h1>
      <input
        type="text"
        placeholder="Filtrar por nombre"
        value={filter}
        onChange={handleFilterChange}
      />
      <button onClick={applyFilter}>Aplicar Filtro</button>
      <ProductList products={products} />
    </div>
  );
};

export default App;
