import React, { useState, useEffect } from 'react';
import ProductList from './productList';

const App = () => {
  const productData = [
    {
      Ean: 'EAN001',
      nombre_producto: 'Producto 1',
      datos_query: [
        { Market: 'Market1', normal_price: 10 },
        { Market: 'Market2', normal_price: 15 }
      ],
      markets_diferentes: new Set(['Market1', 'Market2']),
      rango_precios: [10, 15]
    },
    {
      Ean: 'EAN002',
      nombre_producto: 'Producto 2',
      datos_query: [
        { Market: 'Market2', normal_price: 15 },
        { Market: 'Market1', normal_price: 20 }
      ],
      markets_diferentes: new Set(['Market2', 'Market1']),
      rango_precios: [15, 20]
    },
    {
      Ean: 'EAN003',
      nombre_producto: 'Producto 3',
      datos_query: [
        { Market: 'Market1', normal_price: 20 },
        { Market: 'Market2', normal_price: 30 }
      ],
      markets_diferentes: new Set(['Market1', 'Market2']),
      rango_precios: [20, 30]
    },
  ];

  const [originalProducts, setOriginalProducts] = useState(productData);
  const [visibleProducts, setVisibleProducts] = useState(productData);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    // Configurar el intervalo solo si no se ha aplicado un filtro
    if (filter === '') {
      const interval = setInterval(() => {
        setVisibleProducts((prevVisibleProducts) => {
          if (prevVisibleProducts.length > 0) {
            const newVisibleProducts = [...prevVisibleProducts];
            newVisibleProducts.pop();
            return newVisibleProducts;
          }
          return prevVisibleProducts;
        });
      }, 1000);

      // Limpiar el intervalo al desmontar el componente
      return () => clearInterval(interval);
    }
  }, [filter]);

  const applyFilter = () => {
    const filteredProducts = originalProducts.filter((product) =>
      product.nombre_producto.toLowerCase().includes(filter.toLowerCase())
    );
    setVisibleProducts(filteredProducts);

    // Limpiar el intervalo si se aplica un filtro
    if (filter !== '') {
      clearInterval(interval);
    }
  };

  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  return (
    <div>
      <h1>Lista de Productos</h1>
      <input
        type="text"
        placeholder="Filtrar por nombre"
        value={filter}
        onChange={handleFilterChange}
      />
      <button onClick={applyFilter}>Aplicar Filtro</button>
      <ProductList products={visibleProducts} />
    </div>
  );
};
